class Form{constructor(t){this.form=t,this.btn=this.form.querySelector(".step__btn"),this.sides=this.form.querySelectorAll(".step__choose-side")}nextStep(t){this.btn.setAttribute("data-step",t),this.form.querySelector('.step[data-step="'+t+'"]').classList.add("active"),1==t?this.stepOne(t):2==t&&this.stepTwo(t)}chooseSide(){console.log("asd")}stepOne(t){this.sides.forEach(function(t){t.addEventListener("click",this.chooseSide.bind(this))}),this.btn.addEventListener("click",function(t){t.preventDefault(),2,this.nextStep(2)})}initForm(){this.nextStep(1)}}const form=new Form(document.querySelector(".steps__form"));form.initForm();